mapper.select:
SELECT * FROM (SELECT oi.OUTITEM_NO, oi.ITEM_CD , it.ITEM_NM, oi.QTY , (oi.QTY - tr.RETURN_QTY) AS USABLE_QTY FROM TB_OUTITEM_ITEM oi INNER JOIN TB_ITEMMST it ON oi.ITEM_CD = it.ITEM_CD LEFT OUTER JOIN (SELECT OUTITEM_NO, ITEM_CD, SUM(QTY) RETURN_QTY FROM TB_RETURN WHERE RETURN_DELETE_CHK = 0
AND RETURN_NO != #{return_no}
GROUP BY OUTITEM_NO, ITEM_CD ) tr ON oi.OUTITEM_NO = tr.OUTITEM_NO AND oi.ITEM_CD = tr.ITEM_CD WHERE oi.OUTITEM_NO = #{outitem_no}
AND it.ITEM_NM LIKE '%' + #{keyword} + '%'
) res WHERE (res.USABLE_QTY > 0 OR res.USABLE_QTY IS NULL)

SELECT (oii.QTY - tr.QTY) MAX_QTY
FROM TB_OUTITEM_ITEM oii , (
											SELECT OUTITEM_NO, ITEM_CD , SUM(QTY) AS QTY
											FROM TB_RETURN
											WHERE OUTITEM_NO = #{outitem_no}
												AND ITEM_CD = #{item_cd}
												AND RETURN_NO != #{return_no}
												AND RETURN_DELETE_CHK = 0
											GROUP BY OUTITEM_NO , ITEM_CD
											) tr 
WHERE oii.OUTITEM_NO = tr.OUTITEM_NO 
	AND oii.ITEM_CD = tr.ITEM_CD
