mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY RETURN_NO DESC) AS rn, rt.*, it.ITEM_NM , tc.CUST_NM , us.USER_NM FROM TB_RETURN rt , TB_ITEMMST it , TB_CUSTMST tc, TB_USERMST us WHERE rt.ITEM_CD = it.ITEM_CD AND rt.CUST_CD = tc.CUST_CD AND rt.RETURN_EMP_ID = us.USER_ID AND rt.RETURN_DELETE_CHK = 0 AND rt.RETURN_DT BETWEEN #{start_day} AND #{end_day}
AND ( rt.RETURN_NO LIKE '%' + #{keyword} + '%' OR rt.OUTITEM_NO LIKE '%' + #{keyword} + '%' OR tc.CUST_NM LIKE '%' + #{keyword} + '%' OR it.ITEM_NM LIKE '%' + #{keyword} + '%' OR us.USER_NM LIKE '%' + #{keyword} + '%' )
AND rt.RETURN_NO LIKE '%' + #{keyword} + '%'
AND rt.OUTITEM_NO LIKE '%' + #{keyword} + '%'
AND tc.CUST_NM LIKE '%' + #{keyword} + '%'
AND it.ITEM_NM LIKE '%' + #{keyword} + '%'
AND us.USER_NM LIKE '%' + #{keyword} + '%'
) rt2 WHERE rt2.rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE TB_RETURN
SET RETURN_DELETE_CHK = 1, RETURN_UPDATE = CONVERT(CHAR(10), GETDATE(), 23)
WHERE RETURN_NO = #{return_no}
